def computeStarsItembased(corated, target_bid, model):
    """
    corated - {bid: star, ...}
    """
    if corated == None:
        return None
    corated.pop(target_bid, None)
    bidCor = list(corated.keys())
    collect = []
    for b in bidCor:
        pair = None
        if b < target_bid:
            pair = (b, target_bid)
        else:
            pair = (target_bid, b)
        w = model.get(pair)
        if w != None:
            collect.append((pair, w, b))
    collect.sort(key=lambda x: x[1], reverse=True)
    neighbors = collect[:N_NEIGHBORS_ITEMBASED]
    sum_w = 0
    n = 0
    for (p, w, b) in neighbors:
        star = corated[b]
        n += star * w
        sum_w += w
    if sum_w == 0:
        return None
    else:
        return n / sum_w

