        fname = check_parameters(func, args, kw)
        if obj.is_truncated() or obj.is_canceled() or obj.is_aborted():
            return 0
        if obj.testing_syntax:
            func(obj, *args, calc_time=True, **kw)
            return 0
        obj.debug('{} {} {}'.format(fname, args, kw))
        return func(obj, *args, **kw)
    return decorator

