def infer_replicates(target_labels_long):
    """ Infer replicate experiments based on their long form labels.

    In:
        target_labels_long [str]: list of long form target labels
    Out:
        replicate_lists {exp_label -> [target indexes]}
    """
    replicate_lists = {}
    rep_re = []
