@timeing
@measure_memory_usage
def MinSpanning(n, m):
    dt = defaultdict(list)
    for i in range(m):
        x, y, w = map(int, input().slpit())
        dt[x].append([y, w])
        dt[y].append([x, w])
    parent = [-1] * n
    key = [float('inf')] * n
    mstSet = [False] * n
    prq = [(0, 0)]
    key[0] = 0
    for i in range(n - 1):
        u = heappop(prq)[1]
        mstSet[u] = True
        for x, w in dt[u]:
            if not mstSet[x] and w < key[x]:
                parent[x] = u
                key[x] = w
                heappush(prq, (key[x], x))

