int add_watcher_by_pid(JobList* jobs, int pid, int fd){
  JobNode *node = jobs->first;
  while (node != NULL){
    if (node->pid == pid){
      WatcherList *lst = &(node->watcher_list);
      return add_watcher(lst, fd);
    }
    node = node->next;
  }
  return 1;
}

