static int rp_encode_process_query_response(unsigned int mask,
        const rp_thread_ref *ref,
        const rp_thread_info *info,
        char *out,
        size_t out_size)
{
    size_t len;
    unsigned int tag;
    int i;

    assert(ref != NULL);
    assert(info != NULL);
    assert(out != NULL);
    assert(out_size > 0);

    /* In all cases we will have at least mask and reference thread */
    if (out_size <= 26)
        return 0;

    /* Encode header */
    *out++ = 'q';
    *out++ = 'Q';
    out_size -= 2;

    /* Encode mask */
    sprintf(out, "%08x", mask);
    out += 8;
    out_size -= 8;

    /* Encode reference thread */
    sprintf(out, "%016"PRIu64"x", ref->val);

    out += 16;
    out_size -= 16;

    for (i = 0, tag = 0;  i < 32;  i++, tag <<= 1)
    {
        if ((mask & tag) == 0)
            continue;

        if (out_size <= 10)
        {
            /* We have no place to put even tag and length */
            return 0;
        }

        /* Encode tag */
        sprintf(out, "%08x", tag);
        out += 8;
        out_size -= 8;

        switch (tag)
        {
            case RP_BIT_PROCQMASK_THREADID:
                if (out_size <= 18)
                    return 0;

                /* Encode length - it is 16 */
                rp_encode_byte(16, out);
                out += 2;
                out_size -= 2;

                /* Encode value */
                sprintf(out, "%016"PRIu64"x", info->thread_id.val);

                out += 16;
                out_size -= 16;
                break;
            case RP_BIT_PROCQMASK_EXISTS:
                /* One nibble is enough */
                if (out_size <= 3)
                    return 0;

                /* Encode Length */
                rp_encode_byte(1, out);
                out += 2;
                out_size -= 2;

                /* Encode value */
                *out++    = (info->exists) ? '1' : '0';
                out_size-- ;
                *out      = 0;
                break;
            case RP_BIT_PROCQMASK_DISPLAY:
                /* Encode length */
                len = strlen(info->display);
                assert(len <= 255);

                if (out_size <= (len + 2))
                    return 0;

                rp_encode_byte(len, out);
                out += 2;
                out_size -= 2;

                /* Encode value */
                strcpy(out, info->display);
                out      += len;
                out_size -= len;
                break;
            case RP_BIT_PROCQMASK_THREADNAME:
                /* Encode length */
                len = strlen(info->thread_name);
                assert(len <= 255);

                if (out_size <= (len + 2))
                    return 0;

                rp_encode_byte(len, out);
                out += 2;
                out_size -= 2;

                /* Encode value */
                strcpy(out, info->thread_name);
                out      += len;
                out_size -= len;
                break;
            case RP_BIT_PROCQMASK_MOREDISPLAY:
                /* Encode length */
                len = strlen(info->more_display);
                assert(len <= 255);

                if (out_size <= (len + 2))
                    return 0;

                rp_encode_byte(len, out);
                out += 2;
                out_size -= 2;

                /* Encode value */
                strcpy(out, info->more_display);
                out += len;
                out_size -= len;
                break;
            default:
                /* Unexpected tag value */
                assert(0);
                return 0;
        }
    }

    return 1;
}

