void irq_dis(int nr)
{
	unsigned int addr;
	unsigned char m;

	if (nr < 8)
		addr = A8259A_M + 1;
	else
	{
		addr = A8259A_S + 1;
		nr -= 8;
		if (nr > 7)
			panic("irq_dis: invalid irq number");
	}

	intr_disable();
	m  = inb(addr);
	m |= (1 << nr);
	outb(addr, m);
	intr_enable();
}

